import React, { useState, useContext } from 'react';
import { GlobalContext } from '../context/GlobalState';


const AddTransaction = () => {
  const [text, setText] = useState('');
  const [amount, setAmount] = useState('');
  const { addTransaction } = useContext(GlobalContext);

  const onSubmit = (e) => {
    e.preventDefault()

    const newTransaction = {
      id: Math.floor(Math.random() * 10000000),
      text,
      amount: +amount
    }

    addTransaction(newTransaction)
  }

  return (
    <div className="mt-4 shadow">
      <h3 className="border-bottom">Add new transaction</h3>
      <form onSubmit={onSubmit}>
        <div className="mb-3">
          <label className="form-label" htmlFor="text">Text</label>
          <input className="form-control" type='text' value={text} onChange={(e)=> setText(e.target.value)} placeholder='Enter text...' />
        </div>

        <div className="mb-3">
          <label className="form-label" htmlFor="amount">Amount <br /> 
            (negative - expense, positive - income)
          </label>
          <input className="form-control" type='number' value={amount} onChange={(e)=> setAmount(e.target.value)} placeholder='Enter amount...' />
        </div>
        <button className="btn btn-primary">Add transaction</button>
      </form>
    </div>
  )
}

export default AddTransaction;
